import React, {useEffect, useState} from "react";
import axios from "axios";

function Home() {

  useEffect(() => {
    const [books, setBooks] = useState([]);

    axios.get("http://localhost:3000/")
      .then(books => setBooks(books.data))
      .catch(err => console.log(err));
  }, [])

  return (
    <div>
      <h1>Welcome to the bookstore.</h1>

      <table>
        <tbody>
          {
            books.map(book => {
              return <tr>
                <td>{book.image}</td>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.genre}</td>
                <td>{book.year}</td>
                <td>{book.summary}</td>
                <td>{book.rating}</td>
              </tr>
            })
          }
        </tbody>
      </table>
    </div>
  );
}

export default Home;
