const BookList = ({ books }) => {
	return (
		<table>
      <thead>
        <tr>
          <th>Cover</th>
        </tr>
      </thead>
      <tbody>
  			{books.map((book) => (
          <tr>
    				<td><img className="book-cover" src={book.image} alt="book cover"/></td>
    				<td><p>{book.name} ({book.year})</p></td>
    				<td>
            {book.genre.map((genre, index) => (
    					<p key={genre}>
    						{genre}
    						{index !== book.genre.length - 1 && ", "}
    					</p>
    				))}
            </td>
    				<td><p>{book.rating}</p></td>
          </tr>
  			))}
      </tbody>
		</table>
	);
};

export default BookList;
