import React, { useEffect, useState } from "react";
import axios from "axios";
import BookList from "./BookList";
import Search from "./Search";
import Sort from "./Sort";
import GenreFilter from "./GenreFilter";
import RatingFilter from "./RatingFilter";
import ReviewForm from "./ReviewForm";
import Reviews from "./Reviews";

const base_url = process.env.REACT_APP_API_URL;

function Home() {
  // default values
  const [books, setBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [sort, setSort] = useState({ sort: "rating", order: "desc" });
  const [genreFilter, setGenreFilter] = useState([]);
  const [ratingFilter, setRatingFilter] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const getBooks = async () => {
      try {
        const url = `${base_url}?sort=${sort.sort},${sort.order}&genre=${genreFilter.toString()}&search=${search}`;
        const { data } = await axios.get(url);
        setBooks(data.books);
      } catch (err) {
        console.log(err);
      }
    };

    getBooks();
  }, [sort, genreFilter, search]);

  const handleBookClick = (book) => {
    setSelectedBook(book);
  };

  return (
    <div>
      <h1>Welcome to the bookstore!</h1>
      <Search setSearch={(search) => setSearch(search)} />
      <Sort sort={sort} setSort={(sort) => setSort(sort)} />
      <GenreFilter
        genreOptions={books.genreOptions || []}
        genreFilter={genreFilter}
        setGenreFilter={(genre) => setGenreFilter(genre)}
      />
      <div>
        <BookList books={books} onBookClick={handleBookClick} />
      </div>

      {/* Show book details and review form if a book is selected */}
      {selectedBook && (
        <div>
          <h2>{selectedBook.title}</h2>
          <p>{selectedBook.author}</p>
          <p>{selectedBook.summary}</p>
          <img src={selectedBook.image} alt={selectedBook.title} />
          <ReviewForm bookId={selectedBook._id} />
          <Reviews bookId={selectedBook._id} />
        </div>
      )}
    </div>
  );
}

export default Home;
