import React, { useState } from 'react';
import axios from 'axios';
import './ReviewForm.css'; // Ensure this path is correct

function ReviewForm({ bookId }) {
  const [reviewText, setReviewText] = useState('');
  const [userName, setUserName] = useState('');
  const [rating, setRating] = useState('');

  const handleReviewChange = (e) => {
    setReviewText(e.target.value);
  };

  const handleUserNameChange = (e) => {
    setUserName(e.target.value);
  };

  const handleRatingChange = (e) => {
    setRating(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (reviewText && userName && rating) {
      try {
        await axios.post('/api/reviews', {
          bookId,
          userName,
          rating,
          reviewText,
        });
        setReviewText('');
        setUserName('');
        setRating('');
        // Optionally handle post-submit actions like updating the review list
      } catch (error) {
        console.error('Error submitting review:', error);
        // Optionally handle errors
      }
    } else {
      console.error('Review, username, or rating is empty.');
    }
  };

  return (
    <div className="review-form">
      <h2>Submit a Review</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Your Name:</label>
          <input
            type="text"
            id="username"
            value={userName}
            onChange={handleUserNameChange}
            required
          />
        </div>
        <div>
          <label htmlFor="rating">Rating:</label>
          <input
            type="number"
            id="rating"
            value={rating}
            onChange={handleRatingChange}
            min="1"
            max="5"
            required
          />
        </div>
        <div>
          <label htmlFor="review">Review:</label>
          <textarea
            id="review"
            value={reviewText}
            onChange={handleReviewChange}
            required
          />
        </div>
        <button type="submit">Submit Review</button>
      </form>
    </div>
  );
}

export default ReviewForm;
