import React from "react";

function RatingFilter({ ratingFilter, setRatingFilter }) {
  const onChange = ({ currentTarget: input }) => {
		if (input.checked) {
			const state = [...ratingFilter, input.Number(value)];
			setRatingFilter(state);
		} else {
			const state = ratingFilter.filter((val) => val >= input.Number(value));
			setRatingFilter(state);
		}
	};

  return (
    <div className="center">
      <p>Rating:</p>
        <input type="checkbox" value="5" onChange={onChange} /> 5 & above
        <input type="checkbox" value="4" onChange={onChange} /> 4 & above
        <input type="checkbox" value="3" onChange={onChange} /> 3 & above
        <input type="checkbox" value="2" onChange={onChange} /> 2 & above
        <input type="checkbox" value="1" onChange={onChange} /> 1 & above
    </div>
  )
}

export default RatingFilter;
