import React, { useEffect, useState } from "react";
import axios from "axios";
import BookList from "./BookList";
import Search from "./Search";
import Sort from "./Sort";
import Reviews from "./Reviews"; // Import Reviews component

const base_url = process.env.REACT_APP_API_URL;

function Home() {
  // default values
  const [book, setBooks] = useState({});
  const [sort, setSort] = useState({ sort: "rating", order: "desc" });
  const [search, setSearch] = useState("");
  const [selectedBookId, setSelectedBookId] = useState(null); // State for selected book ID

  useEffect(() => {
    const getBooks = async () => {
      try {
        const url = `${base_url}?sort=${sort.sort},${sort.order}&search=${search}`;
        const { data } = await axios.get(url);

        setBooks(data);
        console.log(data);
      } catch (err) {
        console.log(err);
      }
    };

    getBooks();
  }, [sort, search]);

  return (
    <div>
      <h1>Welcome to the bookstore!</h1>
      <Search setSearch={(search) => setSearch(search)} />
      <Sort sort={sort} setSort={(sort) => setSort(sort)} />
      <div>
        <BookList 
          books={book.books ? book.books : []} 
          setSelectedBookId={setSelectedBookId} // Pass function to set selected book ID
        />
      </div>
      {selectedBookId && <Reviews bookId={selectedBookId} />} {/* Conditionally render Reviews */}
    </div>
  );
}

export default Home;
