const BookList = ({ books }) => {
	return (
		<table>
      <tbody>
        <tr>
    			{books.map((book) => (
    				<div key={book._id}>
    					<div>
    						<img src={book.image} alt="book cover"/>
    						<p>
    							{book.name} ({book.year})
    						</p>
    					</div>
    					<div>
    						{book.genre.map((genre, index) => (
    							<p key={genre}>
    								{genre}
    								{index !== book.genre.length - 1 && ", "}
    							</p>
    						))}
    					</div>
    					<div>
    						<p>{book.rating}</p>
    					</div>
    				</div>
    			))}
        </tr>
      </tbody>
		</table>
	);
};

export default BookList;
