function Sort({ sort, setSort }) {
  return (
    <div>
      <p>Sort by: </p>
      <select
        // defaultValue={sort.sort}
        onChange={onSelectChange}
      >
        <option value="title">Year</option>
        <option value="author">Year</option>
        <option value="rating">Year</option>
        <option value="year">Year</option>
      </select>
    </div>
  )
}

export default Sort;
