import React from "react";
import Grid from "@material-ui/core/Grid";

function BookList({ books, setSelectedBookId }) { // Accept function to set selected book ID

  return (
    <div className="book-list">
      <Grid container spacing={3}>
        {books.map((book) => (
          <Grid item md={3} key={book._id}>
            <img
              className="book-cover"
              src={book.image}
              alt={book.title}
              onClick={() => setSelectedBookId(book._id)} // Set selected book ID
              style={{ cursor: "pointer" }} // Change cursor to pointer
            />
            <p><i>{book.title}</i></p>
            <p>by {book.author}</p>
            <p>{book.rating}</p>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export default BookList;
