import React from "react";

function RatingFilter({ ratingFilter, setRatingFilter }) {
  const onChange = ({ currentTarget: input }) => {
		if (input.checked) {
			const state = [...ratingFilter, input.value];
			setRatingFilter(state);
		} else {
			const state = ratingFilter.filter((val) => val >= input.value);
			setRatingFilter(state);
		}
	};

  return (
    <div className="center">
      <p>Rating:</p>
        <input type="checkbox" value="5" onChange={onChange} />
      ))}
    </div>
  )
}

export default RatinFilter;
